def majority_element nums
#  return if nums.length == 0
  return nums.first if nums.length == 1

  candidate = nums.first
  count = 0
  i = 0
  while i < nums.length
    if count == 0
      candidate = nums[i]
      count += 1
    else
      if candidate == nums[i]
        count += 1
      else
        count -= 1
      end
    end
#    puts "candidate is #{candidate}"
#    puts "nums #{nums[i]}"
#    puts "count is #{count}"
#    puts
    i += 1
  end

#  puts "candidate is #{candidate}"

#  i = 0
#  count = 0
#  while i < nums.length
#    count += 1 if candidate == nums[i]
#    i += 1
#  end

  return candidate 
end

